import { Component, EventEmitter, Output } from '@angular/core';

@Component({
  selector: 'app-nova-transferencia',
  templateUrl: './nova-transferencia.component.html',
  styleUrls: ['./nova-transferencia.component.scss']
})
export class NovaTransferenciaComponent {

  @Output() aoTransferir = new EventEmitter<any>();

  valor: number = 0;
  destino: number = 0;

  transferir(){
    // alert(`Solicitada Nova Transferencia: Valor: ${this.valor} | Destino: ${this.destino}`)
    this.aoTransferir.emit({valor: this.valor, destino: this.destino});
    this.limparCampos();
  }

  limparCampos(){
    this.valor = 0;
    this.destino = 0;
  }
}
<section class="container">
  <form class="formulario" (ngSubmit)="transferir()">
      <h2 class="formulario__titulo">Nova Transferencia:</h2>

      <div class="form-field">
        <label for="valor" class="form-field__label">Valor</label>
        <input id="valor" type="number" class="form-field__input" [(ngModel)]="valor" name="valor">
      </div>

      <div class="form-field">
        <label for="destino" class="form-field__label">Destino</label>
        <input type="number" id="destino" class="form-field__input" [(ngModel)]="destino" name="destino">
      </div>

      <div class="botao-wraper">

        <button class="botao" type="submit">Transferir</button>
      </div>
    </form>

</section>
